if(document.getElementById('sessionTimeout')){let tempoSessao=document.getElementById('sessionTimeout').getAttribute('content')*60;let sessaoExpiradaModal=new bootstrap.Modal(document.getElementById('sessaoExpiradaModal'));let timer=new easytimer.Timer();timer.start({countdown:!0,startValues:{seconds:tempoSessao}});timer.addEventListener('targetAchieved',()=>{sessaoExpiradaModal.show()})}
function exibirConteudoInput(event){let element=document.getElementById(event.target.getAttribute('data-revelar'));let typeInput=element.getAttribute('type')=='password'?'text':'password';element.setAttribute('type',typeInput)}
document.querySelectorAll(".btn-revelar").forEach(function(currentValue,currentIndex,listObj){currentValue.addEventListener("click",exibirConteudoInput)});function modalExcluir(event){let button=event.target;if(button.tagName=='I'){button=button.parentElement}
let id=button.getAttribute('data-excluir-id');let nomeModal=button.getAttribute('data-excluir-modal');let bsModalExcluir=new bootstrap.Modal(document.getElementById(nomeModal)).show();document.getElementById(nomeModal).querySelector('#idExclusao').setAttribute('value',id)}
document.querySelectorAll(".btnExcluir").forEach(function(currentValue,currentIndex,listObj){currentValue.addEventListener("click",modalExcluir)});function limparModalExcluir(event){let form=event.target.getAttribute('data-id-form');document.getElementById(form).querySelector('#idExclusao').setAttribute('value','')}
document.querySelector("#btnCancelarModalExcluir").addEventListener("click",limparModalExcluir)