if(document.getElementById('sessionTimeout')){let tempoSessao=document.getElementById('sessionTimeout').getAttribute('content')*60;let myModal=new bootstrap.Modal(document.getElementById('sessaoExpiradaModal'));let timer=new easytimer.Timer();timer.start({countdown:!0,startValues:{seconds:tempoSessao}});document.getElementById('timerSessao').innerHTML=timer.getTimeValues().toString();timer.addEventListener('secondsUpdated',function(e){document.getElementById('timerSessao').innerHTML=timer.getTimeValues().toString()});timer.addEventListener('targetAchieved',()=>{myModal.show()})}
function exibirConteudoInput(event){let element=document.getElementById(event.target.getAttribute('data-revelar'));let typeInput=element.getAttribute('type')=='password'?'text':'password';element.setAttribute('type',typeInput)}
document.querySelectorAll(".btn-revelar").forEach(function(currentValue,currentIndex,listObj){currentValue.addEventListener("click",exibirConteudoInput)});setTimeout(function(){let alert=bootstrap.Alert.getOrCreateInstance(document.getElementById('alert-do-sistema'));alert.close()},6000)