function getUppercase(){let upperLetters="ABCDEFGHIJKLMNOPQRSTUVWXYZ";return upperLetters[Math.floor(Math.random()*upperLetters.length)]}
function getLowercase(){let lowerLetters="abcdefghijklmnopqrstuvwxyz";return lowerLetters[Math.floor(Math.random()*lowerLetters.length)]}
function getNumber(){let numbers="0123456789";return numbers[Math.floor(Math.random()*numbers.length)]}
function getSymbol(){let symbols="!@#$%^&*()_+=-.,/~][{}:";return symbols[Math.floor(Math.random()*symbols.length)]}
function generateX(){let xs=[];xs.push(getUppercase());xs.push(getLowercase());xs.push(getNumber());xs.push(getSymbol());if(xs.length===0)return"";return xs[Math.floor(Math.random()*xs.length)]}
function generatePassword(event){let len=document.getElementById('tamanho').value;let password="";for(let i=0;i<len;i++){password+=generateX()}
document.getElementById('senhaGerada').value=password}
document.getElementById('tamanho').addEventListener("change",generatePassword);document.getElementById('btn-gerador-senha').addEventListener("click",generatePassword);function aplicaSenha(event){document.getElementById('password').value=document.getElementById('senhaGerada').value;document.getElementById('password').dispatchEvent(new Event('change'))}
document.getElementById('btn-aplica-senha').addEventListener("click",aplicaSenha);function atualizaLabel(event){document.getElementById('labelTamanho').innerHTML='Tamanho: '+document.getElementById('tamanho').value+' caracteres'}
document.getElementById('tamanho').addEventListener("change",atualizaLabel)